function sleep(ms) { return new Promise((resolve) => setTimeout(resolve, ms)); }

module.exports = async function g(indent, x, children) {
  console.log(indent, 'gSTART', x);
  await sleep(300);
  if (children) {
    for (const c of children) {
      await c();
    }
  }
  await sleep(300);
  console.log(indent, 'gEND', x);
};
